import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as s,c as a,a as e,b as o,d as l,e as n,r as i}from"./app.d5425f18.js";const c={},h=n('<p>每日仪式是个人生活习惯的核心。这看起来会固化、死板、功利，有人反馈「透露着一股形式主义气息」，不过，这正是我想要的，<strong>遵循一个固定程序化的流程，有规律的养成习惯</strong>，实现自我提升。</p><h2 id="仪式清单" tabindex="-1"><a class="header-anchor" href="#仪式清单" aria-hidden="true">#</a> 仪式清单</h2><h3 id="每日" tabindex="-1"><a class="header-anchor" href="#每日" aria-hidden="true">#</a> 每日</h3><ul><li><p><strong>建立routine</strong>：</p><ul><li>依托工作带来的固定节奏，调整生活节奏： <ul><li>固定时间吃饭</li><li>每日喝水监控</li><li>每周定期运动，有氧、无氧均有</li></ul></li></ul></li><li><p><strong>间隔学习</strong>：穿插学习不同领域内容</p><ul><li>阅读看书：每天 30 分钟阅读并输出。</li><li>笔记复习：通过 SuperMemo 记忆模型，复习心理认知、沟通理论和行为反思在内的笔记。</li></ul></li><li><p><strong>当日回顾</strong>：记录 3 件好事，总结好事发生的原因，给家人分享好事。</p><ul><li>好事发生：一个可以记录开心事的app</li></ul><blockquote><p>这可以是日常生活中常见的小事，比如读到一本好书、吃到一道好菜、听到一个亲友的好消息、在公交车上有人为你让座、孩子今天会走路了，等等。</p></blockquote></li></ul><h3 id="定期" tabindex="-1"><a class="header-anchor" href="#定期" aria-hidden="true">#</a> 定期</h3>',5),u=e("strong",null,"【转载】月底反思",-1),d={href:"https://allisonseboldt.com/",target:"_blank",rel:"noopener noreferrer"},_={href:"https://justinjackson.ca/bootstrap",target:"_blank",rel:"noopener noreferrer"},p=e("h3",{id:"记录",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#记录","aria-hidden":"true"},"#"),o(" 记录")],-1),f={href:"https://newzone.top/",target:"_blank",rel:"noopener noreferrer"},b={href:"https://newzone.top/_posts/2022-05-22-surveillance_video_for_myself.html",target:"_blank",rel:"noopener noreferrer"},k=e("li",null,[e("p",null,"微习惯视频：监督运动习惯。拍摄视频时，使用关键句「切掉/保留」，配合 Quickcut 快速剪辑视频。")],-1),m=n('<h2 id="时间管理" tabindex="-1"><a class="header-anchor" href="#时间管理" aria-hidden="true">#</a> 时间管理</h2><blockquote><p>在限定的时间内，做好更多重要的事。<sup class="footnote-ref"><a href="#footnote1">[1]</a><a class="footnote-anchor" id="footnote-ref1"></a></sup></p></blockquote><p>怎么来评价一件事重不重要？</p><ul><li>思路一：看做好这件事的结果是否重要。 比如这件事关系到领导的年度任务，比如这是个重要的大客户，那自然是重要。</li><li>思路二：看不做好这件事会有什么后果。 比如大领导过来视察，虽然接待好了不一定会带来多大的业绩（你总不能在年终总结上说今年接待了 XX 位领导），但不做好肯定会让自己吃亏。</li><li>思路三：看这件事对自己的价值。 这种价值是自我成长层面上的。比如参加一场公司内部培训，它会占用你的工作时间，甚至可能影响到业绩；但是，它有利于你个人的知识储备，对个人未来的发展有帮助，那就是重要的。</li></ul><hr class="footnotes-sep">',5),g={class:"footnotes"},x={class:"footnotes-list"},v={id:"footnote1",class:"footnote-item"},q={href:"https://sspai.com/post/61724",target:"_blank",rel:"noopener noreferrer"},N=e("a",{href:"#footnote-ref1",class:"footnote-backref"},"↩︎",-1);function V(y,B){const t=i("ExternalLinkIcon");return s(),a("div",null,[h,e("ul",null,[e("li",null,[u,o("：检查日记和成果，对当月进行复盘，并将其同步给家人，征求意见、批评并记录。 "),e("ul",null,[e("li",null,[o("复盘笔记参照 "),e("a",d,[o("Allison Seboldt"),l(t)]),o("，作者每个月都会进行「回顾」，分享他的收入并反思上个月的成就。")]),e("li",null,[o("读 "),e("a",_,[o("JustinJ ackson"),l(t)]),o(" – 作者在 2018 年初创办  Transistor  以来，一直在分享他的项目。他整理了在此过程中学到的许多经验教训。")])])])]),p,e("ul",null,[e("li",null,[e("p",null,[e("a",f,[o("学习笔记"),l(t)]),o("：将对工具、工作方式和生活的思考记录发布在公开页面，欢迎大家的点评。")])]),e("li",null,[e("p",null,[o("工作视频："),e("a",b,[o("自我监控视频"),l(t)]),o("，记录私密工作状态，方便当日回顾。")])]),k]),m,e("section",g,[e("ol",x,[e("li",v,[e("p",null,[e("a",q,[o("我的时间管理办法（2020 版）"),l(t)]),o(),N])])])])])}const j=r(c,[["render",V],["__file","DailyRoutine.html.vue"]]);export{j as default};
