import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as n,c as s,a as e,b as o,d as a,e as l,r as c}from"./app.f32cb56f.js";const i={},f=e("h2",{id:"自身健康",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#自身健康","aria-hidden":"true"},"#"),o(" 自身健康")],-1),h={href:"https://detail.tmall.com/item.htm?id=542176943787",target:"_blank",rel:"noopener noreferrer"},d={href:"https://detail.tmall.com/item.htm?id=568724954216",target:"_blank",rel:"noopener noreferrer"},_=l('<h2 id="维生素" tabindex="-1"><a class="header-anchor" href="#维生素" aria-hidden="true">#</a> 维生素</h2><p>维生素：维生素 B 和 C 国内非常便宜，几元一瓶。但其他维生素国内只有保健品，价格严重虚高，优先考虑国外购买。</p><p>护眼：叶黄素，玉米黄质，Lutemax® 2020 万寿菊花提取物。</p><p>补钙：吃钙片要同时补充维他命 D 和维他命 K。钙本身是无法直接被身体吸收的，维他命 D 的作用是运输，带动钙元素在血液中流动。但光流动不行，需要给钙找个目标，才能把钙变成骨骼。所以就需要维他命 K，维他命 K 的作用则是把血钙转化为骨钙，让钙元素真正沉积在骨骼上。D 和 K 相结合，才能最大程度地提升钙的吸收率。</p><h2 id="看病技巧" tabindex="-1"><a class="header-anchor" href="#看病技巧" aria-hidden="true">#</a> 看病技巧</h2><ul><li><p>如果在网上挂不到专家的号，可以尝试电话联系科室问一下能不能当天加号，有的专家是允许加号的，只不过可能会加到当天比较晚的时候，出检查结果的时候专家就下班了。<sup class="footnote-ref"><a href="#footnote1">[1]</a><a class="footnote-anchor" id="footnote-ref1"></a></sup></p></li><li><p>如果对自己病情没有很多了解的话，建议早上空腹去，以免抽不了血。如果对自己要做的检查有大致了解的话，建议查一下注意事项，提前做好准备，比如做核磁共振身上不要带金属，女生最好穿无钢圈无挂钩的 bra，比如散瞳后不要驾驶等等。</p></li><li><p>人这一生中还是得常见病的概率大，没必要迷信主任医师。建议首次看病不必找资历太高的医生，如果你的病真的到了需要找老大夫看看的地步，年轻大夫会领着你去找的。</p></li><li><p>上午看病的人多，下午少，周一多，周五少，尽量避开周一上午。刮风下雨是看病的最佳时机，雨越大，人越少。<sup class="footnote-ref"><a href="#footnote2">[2]</a><a class="footnote-anchor" id="footnote-ref2"></a></sup></p></li></ul><hr class="footnotes-sep">',7),p={class:"footnotes"},m={class:"footnotes-list"},u={id:"footnote1",class:"footnote-item"},k={href:"https://www.guokr.com/article/461939/",target:"_blank",rel:"noopener noreferrer"},b=e("a",{href:"#footnote-ref1",class:"footnote-backref"},"↩︎",-1),x={id:"footnote2",class:"footnote-item"},g={href:"https://www.guokr.com/article/461706/",target:"_blank",rel:"noopener noreferrer"},w=e("a",{href:"#footnote-ref2",class:"footnote-backref"},"↩︎",-1);function B(K,N){const t=c("ExternalLinkIcon");return n(),s("div",null,[f,e("p",null,[e("a",h,[o("尿素乳膏"),a(t)]),o("：柔软皮肤，防止干裂，深层滋润。")]),e("p",null,[e("a",d,[o("维 a 酸乳膏"),a(t)]),o("：更新表皮，去除角质，避光只能晚上用。")]),_,e("section",p,[e("ol",m,[e("li",u,[e("p",null,[e("a",k,[o("不用迷信主任，要是真有大病，年轻大夫会领着你去找的……"),a(t)]),o(),b])]),e("li",x,[e("p",null,[e("a",g,[o("“为了您和家人的健康，请不要在星期一上午看病”，超实用看病指南"),a(t)]),o(),w])])])])])}const D=r(i,[["render",B],["__file","Medical.html.vue"]]);export{D as default};
